import React from 'react'
import './product.css'


const Products = ({ state, dispatch }) => {
    
    const { products, cart } = state;
    

    return (
        <div className='products'>
            {products.map((prod) => {
                return (
                    <div
                        key={prod.id}
                        className="card"
                    >
                        <img src={prod.thumbnail}
                            alt={prod.title}
                            className="image"
                        />
                        <div style={{ display: "flex", justifyContent: "space-between" }}>
                            <span>{prod.title}</span>
                            <b>â‚¹{prod.price}</b>
                        </div>

                        {cart.some((p)=>p.id === prod.id)?
                        (<button
                            style={{
                                backgroundColor: "#f55d5d",
                                color: "white",
                                padding: 5,
                                borderRadius: 7,
                                border: 0
                            }}
                            onClick={()=>{
                                
                                dispatch({
                                    type:"REMOVE_FROM_CART",
                                    payload:prod
                                })
                            }}
                        >
                            Remove From Cart
                        </button>) :
                            (
                        <button style={{
                            backgroundColor: "#11ba44",
                            color: "white",
                            padding: 5,
                            borderRadius: 7,
                            border: 0
                        }}
                        onClick = {()=>{
                            console.log("state",state)
                            dispatch({
                                id :prod.id,
                                title:prod.title,
                                thumbnail:prod.thumbnail,
                                price:prod.price,
                                qty:1,                               
                            }) 
                        }}
                        >
                            Add To Cart
                        </button>

                        )   
                    }
                        

                    </div>
                );
            })}
        </div>
    )
}
export  default Products
