import React from "react";
import "./product.css";

const Products = ({ state, dispatch }) => {
  const { products, cart } = state;
  console.log("cart", cart);
  return (
    <div className="products">
      {products.map((item) => (
        <div
          key={item.id}
          style={{
            display: "flex",
            flexDirection: "column",
            padding: 10,
            border: "0px solid grey",
            width: "30%",
            marginTop: 10,
            gap: 10
          }}
        >
          <img
            src={item.thumbnail}
            alt={item.title}
            style={{
              height: 200,
              objectFit: "cover",
              borderRadius: "5px"
            }}
          />
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <span>{item.title}</span>
            <b>₹{item.price}</b>
          </div>

          {cart.some((p) => p.id === item.id) ? (
            <button
              style={{
                padding: 5,
                border: 0,
                borderRadius: 5,
                backgroundColor: "red",
                color: "white"
              }}
              onClick={() => {
                dispatch({
                  type: "REMOVE_FROM_CART",
                  payload: item
                });
              }}
            >
              Remove From Cart
            </button>
          ) : (
            <button
              style={{
                padding: 5,
                border: 0,
                borderRadius: 5,
                backgroundColor: "green",
                color: "white"
              }}
              onClick={() =>
                dispatch({
                  type: "ADD_TO_CART",
                  payload: {
                    id: item.id,
                    title: item.title,
                    thumbnail: item.thumbnail,
                    qty: 1,
                    price: item.price
                  }
                })
              }
            >
              Add To Cart
            </button>
          )}
        </div>
      ))}
    </div>
  );
};

export default Products;

