import { padding } from '@mui/system'
import React from 'react'
import './product.css'

export const Products = ({ state, dispatch }) => {
    const { products, cart } = state
    //   console.log("produvts",state.products)

    return (
        <div className='products'>
            {products.map((prod) => {
                return  (
                    <div
                    key={prod.id}
                    className="card"
                    >
                    <img src={prod.thumbnail}
                        alt={prod.title}
                        className="image"
                         />
                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                        <span>{prod.title}</span>
                        <b>{prod.price}</b>
                    </div>
                    <button style={{
                        backgroundColor:"green",
                        color:"white",
                        padding:5,
                        borderRadius:2
                        }}>
                        Add To Cart
                    </button>
                    <button>
                        Remove From Cart
                    </button>

                </div>
                    );
            })}
        </div>
    )
}
